a = result
list = []

for (i = 0; i < a.length; i++) {
    java.setContent(a[i])
    b = java.getString("tag.a@href")
    if (b == "javascript:cid(0)") {
        if (i == a.length - 1) {
            java.setContent(a[i - 1])
            c1 = java.getString("tag.a@href")
            if (c1 == "") {
                java.setContent(a[i - 2])
                c1 = java.getString("tag.a@href")
            }
            d1 = java.ajax("https://w.linovelib.com" + c1)
            java.setContent(d1)
            e1 = java.getElements("id.footlink")

            while (e1.indexOf("下一页") != -1) {
                f1 = d1.match(/<script type\=\"text\/javascript\">var ReadParams.*/)
                g1 = String(f1).match(/url_next\:'.*?html/)[0].replace("url_next:'", "")
                d1 = java.ajax("https://w.linovelib.com" + g1)
                java.setContent(d1)
                e1 = java.getElements("id.footlink")
            }

            f1 = d1.match(/<script type\=\"text\/javascript\">var ReadParams.*/)
            g1 = String(f1).match(/url_next\:'.*?html/)[0].replace("url_next:'", "")
            java.log(g1)
            a[i] = String(a[i]).replace('javascript:cid(0)', g1)

        } else {
            java.setContent(a[i + 1])
            c = java.getString("tag.a@href")
            if (c == "") {
                java.setContent(a[i + 2])
                c = java.getString("tag.a@href")
            }
            d = java.ajax("https://w.linovelib.com" + c)
            e = d.match(/<script type\=\"text\/javascript\">var ReadParams.*/)
            e = String(e).match(/url_previous\:'.*?html/)[0].replace("url_previous:'", "")
            java.log(e)

            a[i] = String(a[i]).replace('javascript:cid(0)', e)
        }
    }
}
a

