$.id@js:
var Magua = new JavaImporter();
Magua.importPackage(
Packages.java.lang,
Packages.java.security,
);
with(Magua) {
  function strToMd5(str) {
    let md5 = MessageDigest.getInstance("MD5");
    let bytes = md5.digest(String(str).getBytes());
    let stringBuffer = new StringBuilder();
    let bt = null;
    bytes.forEach(a => {
      bt = a & 0xff;
      if (bt < 16) {
        stringBuffer.append("0");
      }
      stringBuffer.append(Integer.toHexString(bt));
    });
    return stringBuffer.toString();
  }
}
function getSig(timestamp, params) {
  return strToMd5("45667191c50941a1a777dfbd84345fe8" + timestamp + params);
}
function randomNum(min, max) {
  switch (arguments.length) {
    case 1:
      return Math.floor(Math.random() * minNum + 1);
    case 2:
      return Math.floor(Math.random() * (max - min + 1) + min);
    default:
      return 0;
  }
}

var timestamp = new Date().getTime();
var params = JSON.stringify({
device_id: String(randomNum(10000000, 19999999)),
id: parseInt(result),
size_type: "webp"
}); "http://api.ilikemanga.com/request.php?method=infov2&timestamp="+timestamp+"&param="+params+"&sig="+getSig(timestamp,params)

