	* 2.重构图片请求头和解密
	*/
//下面不要修改任何内容
let domain = String(source.getKey()).split(".")[1];
/**
 *@params options {Options|null}
 */
Object.prototype.addOptions=function(options){let j={"Referer":baseUrl.replace(/,[\s\S]+\}/,"")};if(!this.toString().includes(domain)){j.Referer=""};options=options||{};options.headers=Object.assign(options.headers||{},j);return options?`${this.toString()},${JSON.stringify(options)}`:this.toString()};
/**
 *@params func {Function|null}
 *@params options {Options|null}
 */
Object.prototype.toImg=function(func,options){
	let j=(u)=>`<img src="${u.addOptions(options)}">`,h=(u,i,t)=>typeof func=="function"?j(func(u,i,t)):j(u);return Array.isArray(this)?this.map(h).join("\n"):h(this.toString())};
function load(t){for(let o of t){let j = java.ajax(o);if(!/exception/.test(j))return[j,o]}throw"Error: 所有链接下载失败"}
//阅读后台WebView解析密钥
function parseKeys(){
jsBaseUrls=[
"https://raw.githubusercontents.com/Xwite/decodeObfuscator/main/", 
