"plugins/cocomanhua.js",
"tools/babel_asttool.js",
"tools/config.js",
"tools/decode.js",
"tools/decodeOb.js",
"tools/usefulPlugins.js",
];
jsBaseUrl = cache.get(`sourceVariable_${source.getKey()}/`)
if (!jsBaseUrl) {
jsBaseUrl = load(jsBaseUrls)[1];
java.toast(jsBaseUrl);
cache.put(`sourceVariable_${source.getKey()}/`, jsBaseUrl)
}
String.prototype.toAbsUrl=function(){
	return /^\.\/|^http|^content|^\/sdcard|^\/storage/.test(this) ? this : jsBaseUrl.concat(this)
};
java.log(jsBaseUrl);
String.prototype.toAbsUrl=function(){
	return /^\.\/|^http|^content|^\/sdcard|^\/storage/.test(this) ? this : jsBaseUrl.concat(this)
}
//解决跨域js加载问题
function loadJS(urls){

	return jsBaseUrl && urls.map(url=>script(java.importScript(url.toAbsUrl())))
	.join("\n") || ""
	}

function script(content){
	return `<script>${content};</script>`
	}
	java.log(">>>>> 本地密钥解密开始");
//html片段
